const arr = JSON.parse(stringQues);

function createTextQues(no, text) {
  const h3 = document.createElement("h3");
  h3.innerText = "Câu " + no + " : " + text;
  return h3;
}

function createChoices(no, choices) {
  
  return choices
    .filter((choice) => !choice.toLowerCase().includes("answer"))
    .map((choice) => {
      const input = document.createElement("input");
      input.type = "radio";
      input.name = `#question${no}`;

      const text = document.createElement("p");
      text.innerText = choice;

      const div = document.createElement("div");
      div.classList.add("choice");

      div.appendChild(input);
      div.appendChild(text);
      return div;
    });
}

function createQuestion(no, ques) {
  const div = document.createElement("div");
  div.classList.add("question");
  div.id = `#question${no}`;
  
  const choices = document.createElement("div");
  choices.classList.add("choices");
  choices.append(...createChoices(no, ques.choices).map(it => {
    it.addEventListener("click", (e) => {
      const selected = it.querySelector("input");
      selected.checked = true;
      const text = it.querySelector("p");
      const noQues = parseInt(selected.name.split("#question")[1]);

      if(arr[noQues - 1].correctId === "") {
        alert("Chưa có đáp án")
        return;
      }
      const choices = it.parentElement.querySelectorAll(".choice p");
      choices.forEach((choice, id) => {
        choice.classList.remove("correct");
        choice.classList.remove("wrong");
      });

      if(arr[noQues - 1].correctId === text.innerText[0]) {
        text.classList.add("correct");
      } else {
        text.classList.add("wrong");
      }
      text.style.fontWeight = "600";
    });
    return it;
  }));
  div.appendChild(createTextQues(no, ques.text));
  div.appendChild(choices);

  return div;
}

const buildQuizLSD = (quiz) => {
  const container = document.querySelector("#LSD-quiz .quiz");
  quiz.forEach((item, no) => {
    container.appendChild(createQuestion(no + 1, item));
  });
}

buildQuizLSD(arr);

const randomQues = () => {
  for (let i = arr.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    // Swap arr[i] and arr[j]
    [arr[i], arr[j]] = [arr[j], arr[i]];
  }
  return arr;
}

document.querySelector("#LSD-quiz .randomQuiz").addEventListener("click", () => {
  const container = document.querySelector("#LSD-quiz .quiz");
  container.innerHTML = "";
  buildQuizLSD(randomQues());
});