tthcm = JSON.parse(tthcm);

function shuffleArray(array) {
    for (let i = array.length - 1; i > 0; i--) {
        const j = Math.floor(Math.random() * (i + 1));
        // Swap array[i] and array[j]
        [array[i], array[j]] = [array[j], array[i]];
    }
    return array;
}

tthcm = shuffleArray(tthcm);
function createRadio(id) {
    var radio = document.createElement("input");
    radio.type = "radio";
    radio.name = id.toString();
    radio.classList.add(id.toString());
    return radio;
}

function createP(text, id, tag) {
  var p = document.createElement("p");
  p.innerHTML = `<b>${tag}.</b> \t\t` + `<p>${text}</p>`
  p.style.display = 'flex';
  p.style.alignItems = 'center'
  p.classList.add(id.toString());
  return p;
}



tthcm.forEach((r, id) => {
    var div_question = document.createElement("div");
    var div_ques = document.createElement("div");
    var div_choices = document.createElement("div");
    div_ques.textContent = `${id + 1}. \t\t` + r.ques;
    div_ques.style.fontSize = "20";
    div_ques.style.fontWeight = "bold"

    
    
    var tagChoice = ["A", "B", "C", "D"];
    for(let i = 0; i < tagChoice.length; i++) {
        var div_choice = document.createElement("div");
        var radio = createRadio(id);
        var p = createP(r.choices[i], id, tagChoice[i]);
        div_choice.classList.add("choice")
        div_choice.appendChild(radio);
        div_choice.appendChild(p);
        div_choices.appendChild(div_choice);
    }

    div_question.appendChild(div_ques);
    div_question.appendChild(div_choices);

  document.querySelector("#TTHCM-quiz").appendChild(div_question);
 
});

var all_input = document.querySelectorAll("#TTHCM-quiz input");

all_input.forEach((ai) => {
  ai.addEventListener("click", (e) => {
    var name = e.target.name;

    //console.log(parseInt(name, 10));
    var ans = tthcm[parseInt(name, 10)].ans;
    //console.log(ans);
    ans = ans.toUpperCase();

    if (ans === "") {
      alert("Câu hỏi này không có đáp án");
    }
    var text_checked = e.target.parentNode.querySelector("p p").textContent.toUpperCase();
    // console.log(ans, name, text_checked,text_checked.includes(ans));
    // console.log(ans, text_checked)
    document.querySelectorAll("p").forEach((p) => {
      if (p.classList.contains(name)) {
        p.style.color = "black";
        p.style.fontWeight = "normal";
      }
    });

    e.target.parentNode.childNodes.forEach((child) => {
      if (child.tagName === "P") {
        if (text_checked === ans) {
          child.style.color = "green";
          child.style.fontWeight = "bold";
        } else {
          child.style.color = "red";
          child.style.fontWeight = "bold";
        }
      }
    });

  });
});

var all_p = document.querySelectorAll("#TTHCM-quiz p");

all_p.forEach((ap) => {
  ap.addEventListener("click", (e) => {
    var parent = e.target.parentNode.parentNode;
    parent.childNodes.forEach((child) => {
      if (child.tagName === "INPUT") {
      //  console.log(child.tagName);
        child.click();
      }
    });
  });
});